package leetcode72;

import java.util.HashMap;

/**
 * @ClassName: Solution7202.java
 * @Description:统计法 
 * 
 * @author  jixiang
 * @version v1.0.0 
 * @Date    2020年11月23日 下午3:07:36 
 */
public class Solution7202 {

	public boolean judgeCircle(String moves) {
		HashMap<Character, Integer> map = new HashMap<Character, Integer>();
		for (int i = 0; i < moves.length(); i++) {
			map.put(moves.charAt(i), map.getOrDefault(moves.charAt(i), 0)+1);
		}
		return map.getOrDefault('L', 0).equals(map.getOrDefault('R', 0))
				&&map.getOrDefault('U',0).equals(map.getOrDefault('D',0));
	}
	
	public static void main(String[] args) {
		Solution7202 sol = new Solution7202();
		String commad = "UD";
		System.out.println("IsCircle :" +commad + sol.judgeCircle(commad));
		
		String commad1 = "LL";
		System.out.println("IsCircle :" +commad1 + sol.judgeCircle(commad1));
		
		String commad2 = "";
		System.out.println("IsCircle :" +commad2 + sol.judgeCircle(commad2));
	}
}
