public class Solution7203 {
	
	public boolean judgeCircle(String moves) {
		int[] words = new int[26];
		for (int i = 0; i < moves.length(); i++) {
			words[moves.charAt(i)-'A']++;
		}
		return words['L'-'A']==words['R'-'A']&&words['U'-'A']==words['D'-'A'];
	}
	
	public static void main(String[] args) {
		Solution7201 sol = new Solution7201();
		String commad = "UD";
		System.out.println("IsCircle :" +commad + sol.judgeCircle(commad));
		
		String commad1 = "LL";
		System.out.println("IsCircle :" +commad1 + sol.judgeCircle(commad1));
		
		String commad2 = "";
		System.out.println("IsCircle :" +commad2 + sol.judgeCircle(commad2));
	}

}
